INSERT INTO COFFEE (name, description, roast_type, stock, price)
VALUES ('Kavos pupelės Coffee Cruise SWEET BRAZIL', 'Sweet Brazil skonis švelnus ir itin malonus. Geriant šią kavą jaučiamas juodo šokolado kartumas patiks tiems kavos mėgėjams, kurie save palepina keliais puodeliais dienos eigoje. Ilgas kavos pupelių paruošimo laikotarpis suteikia jai brandumo, solidumo, tų gerųjų skoninių savybių, kurias itin vertina tikri kavos gurmanai.', 'light', 2500, 2.7);
INSERT INTO COFFEE (name, description, roast_type, stock, price)
VALUES ('Kavos pupelės Coffee Cruise PAPUA NEW GUINEA', 'Papua New Guinea kava turi malonią imbiero ir medaus skonių harmoniją, kurią papildo džiovintų slyvų ir razinų natos. Intensyvi, aitrumo ir saldumo turinti kava puikiai nuteikia dienai ryte ir suteikia taip reikalingos energijos popietę.', 'medium', 1500, 3.1);
INSERT INTO COFFEE (name, description, roast_type, stock, price)
VALUES ('Kavos pupelės Coffee Cruise TANZANIA', 'Tanzanijoje augančios kavos uogos išsiskiria savo dydžiu, pakankamai dažnu derliumi. Kavos skonis palieka gilų įspūdį Ši kava rekomenduojama mėgstantiems sodrią kavą, kurioje jaučiamas kartumas, šiek tiek saldumo bei vaisinio rūgštumo. Tobulai derančios šokolado ir vanilės natos, citrusų vaisiškumas - visa tai telpa Tanzanijos kavoje.', 'dark', 2000, 3.3);
INSERT INTO COFFEE (name, description, roast_type, stock, price)
VALUES ('Kavos pupelės Coffee Cruise COLOMBIA', 'Columbios kavoje dera kartumas, vaisinis rūgštumas ir karamelinis saldumas. Lengvo skonio vidutiniškai skrudinta kava dažnai pasirenkama tokių kavos gėrimų kaip Latte, Latte Machiato ar Capuccino gerbėjų. Žinoma, nieko nėra puikiau už stiprų rytinį espresso puodelį.', 'light', 1000, 2.9);
INSERT INTO COFFEE (name, description, roast_type, stock, price)
VALUES ('Kavos pupelės Coffee Cruise EL SALVADOR', 'Šiame regione augančiose kavos pupelėse dominuoja razinoms būdingas vos jaučiamas kartumas, kurį papildo klevų sirupo natos. Ši kompozicija leidžia mėgautis intensyvia, emocinga, subalansuoto skonio kava. Vaisiškumo kavai suteikia sultinga vyšnia, o įkvėpus aromato pajusite vynuogių laukuose tvyrantį karštį.', 'medium', 3000, 3.1);
